//define utils
const utils = {}
utils.typeName = function(val) {
    return Object.prototype.toString.call(val).slice(8, -1);
};
utils.close = function(context, func, params) {
    return function() {
        var args = params || arguments;
        return func.apply(context, args);
    };
};
// export {utils}
module.exports = utils