const expect = require('chai').expect
const Promise = require('../index.js')


describe('Promise.reject', () => {
	it('a string reason', done => {
		Promise.reject('reject by god')
				.catch(ee => {
					done()
				})
	})
	it('a object reason', done => {
		Promise.reject({e:2})
				.catch(ee => {
					done()
				})
	})
})