class Cookie {
    parse () {
        const cookieArr = document.cookie.split(';')
        let cookieMap = {};
        cookieArr.forEach(function(item){
            if (item.split('=').length > 1) {
                cookieMap[item.split('=')[0].trim()] = unescape(item.split('=')[1].trim())
            }
        })
        return cookieMap
    }
    set (c_name, value, time = 0, domain) {
        if (!Number(time)) time = null
        const expires = !time ? "" : ";expires=" + new Date(new Date().getTime() + Number(time)).toGMTString()
        domain = !domain ? '' : ';domain=' + domain
        document.cookie=c_name+ "=" + escape(value) + domain + expires + ';path=/'
    }
}
export default new Cookie
