class Query  {
    parse (query){
        query = query.replace(/^\?/,'')
        const arr = query.split(/&/)
        let result = {}
        for(let i=0; i<arr.length; i++){
            const keyvalue = arr[i].split(/\=/)
            result[keyvalue[0]] = keyvalue[1]
        }
        return result
    }
    queryify (json,prefix) {
        prefix = prefix || "?"
        let query = prefix
        for(let key in json){
            query += (key + '=' +json[key]) + '&'
        }
        return query.replace(/\&$/,'')
    }
}
export default new Query;
