let url=''

let isuat = true

let domain = 'https://m.gome.com.cn'

typeof window === 'object'?url=window.location.href:url=weex.config.bundleUrl

if(url.match(/gome\.com\.cn|gomeplus\.com/)){
    isuat = false
}

if(isuat){
    domain = 'http://m.uatplus.com'
}

export default {
    domain:domain
}