const path = require('path')
const webpack = require('webpack')

module.exports = {
   entry:'./src/index.js',
   output:{
       path:path.resolve(__dirname,'dist'),
       filename:'weexModule.js',
       libraryTarget:'umd',
       library:'woox'
   },
   module:{
       loaders:[
           {
               test:/\.js$/,
               loader:'babel-loader'
           },
           {
            test: /\.css$/,
            loader: 'style-loader!css-loader'
           }
       ]
   },
   plugins:[
       new webpack.optimize.UglifyJsPlugin({
           compress:{
               warnings:false
           }
       })
   ]
}